	public static final String getParagraph (int word, boolean startWithLorem, boolean punctuation) {
		StringBuffer outParagraph = new StringBuffer();
		
		int countWord = 0;
		int LoremPos = 0;
		if (!startWithLorem) {
			LoremPos = (int)Math.round(Math.random()*LoremIpsum.length()-1);
		}
		
		while (countWord < word) {
			char c = LoremIpsum.charAt(LoremPos);
			if (c==' ') {
				countWord++;
			}
			if ((punctuation && countWord > 5) || !Arrays.asList(new Character[] {',', ';', '.', ':'}).contains(c)) { //  countWord > 5 -> no punctuation in 5 first word
				if (c!=' '||countWord>1) { // not start with a space
					outParagraph.append(c);		
				}
			}			
			LoremPos++;
			if (LoremPos>=LoremIpsum.length()) {
				LoremPos = 0;
				countWord++;
				outParagraph.append(' ');	
			}
		}
		
		return outParagraph.toString();
		
	}
	
	public static void main(String[] args) {
		System.out.println("**** test 4 word = "+getParagraph(4, false, false));
		System.out.println("**** test 12 word = "+getParagraph(12, true, true));
		System.out.println("**** test 12 word = "+getParagraph(12, true, false));
	}
